CREATE TABLE IF NOT EXISTS "catalog_reviewimage" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "image" varchar(100) NULL, "review_id" bigint NULL REFERENCES "catalog_review" ("id") DEFERRABLE INITIALLY DEFERRED, "alt_text" varchar(255) NOT NULL, "created_at" datetime NOT NULL);
CREATE TABLE IF NOT EXISTS "accounts_useraddress" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "address_line" varchar(255) NULL, "city" varchar(100) NULL, "state" varchar(100) NULL, "postal_code" varchar(20) NULL, "user_id" bigint NOT NULL REFERENCES "accounts_user" ("id") DEFERRABLE INITIALLY DEFERRED, "country" varchar(100) NOT NULL);
CREATE TABLE IF NOT EXISTS "orders_coupon" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "code" varchar(50) NOT NULL UNIQUE, "discount_percent" integer unsigned NOT NULL CHECK ("discount_percent" >= 0), "active" bool NOT NULL, "created_at" datetime NOT NULL, "description" text NOT NULL, "discount_amount" decimal NOT NULL, "max_uses" integer unsigned NULL CHECK ("max_uses" >= 0), "min_order_amount" decimal NOT NULL, "name" varchar(100) NOT NULL, "updated_at" datetime NOT NULL, "used_count" integer unsigned NOT NULL CHECK ("used_count" >= 0), "valid_from" datetime NULL, "valid_until" datetime NULL);
