CREATE TABLE IF NOT EXISTS "catalog_productcollection" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "order" integer unsigned NOT NULL CHECK ("order" >= 0), "featured_until" datetime NULL, "added_at" datetime NOT NULL, "collection_id" bigint NOT NULL REFERENCES "catalog_collection" ("id") DEFERRABLE INITIALLY DEFERRED, "product_id" bigint NOT NULL REFERENCES "catalog_product" ("id") DEFERRABLE INITIALLY DEFERRED);
